INSERT INTO shops (category_id, name, short_description, detailed_description, working_hours, floor, image_url, features, visitor_tips) VALUES
    (1, 'Zara', 'Модная одежда для всей семьи.', 'Zara — ведущий мировой бренд модной одежды, расположенный на 2-м этаже ТРЦ Атриум (Земляной Вал, 33). Магазин предлагает стильные коллекции для мужчин, женщин и детей.', '10:00–22:00', '2-й этаж', 'zara_store.jpg', 'Еженедельное обновление коллекций, экологичные материалы.', 'Проверяйте новые поступления по вторникам и пятницам. Используйте онлайн-резерв через сайт Zara.'),
    (1, 'Nike', 'Спортивная одежда и обувь.', 'Nike на 2-м этаже ТРЦ Атриум предлагает широкий ассортимент спортивной одежды, обуви и аксессуаров для активного образа жизни.', '10:00–22:00', '2-й этаж', 'nike_store.jpg', 'Персонализация кроссовок, программа лояльности.', 'Записывайтесь на консультацию по подбору обуви. Проверяйте акции в приложении Nike.'),
    (1, 'Bershka', 'Молодёжная мода.', 'Bershka на 2-м этаже ТРЦ Атриум — это трендовая одежда для молодёжи с акцентом на уличный стиль.', '10:00–22:00', '2-й этаж', 'bershka_store.jpg', 'Сезонные распродажи, яркие коллекции.', 'Приходите в будни утром для комфортного шопинга.'),
    (1, 'Adidas', 'Спортивные товары.', 'Adidas на 2-м этаже ТРЦ Атриум предлагает спортивную одежду, обувь и экипировку для всех возрастов.', '10:00–22:00', '2-й этаж', 'adidas_store.jpg', 'Эксклюзивные модели, программа лояльности.', 'Используйте приложение Adidas для бронирования новинок.')
INSERT INTO food (category_id, name, short_description, detailed_description, working_hours, floor, image_url, cuisine_type, features, visitor_tips) VALUES
    (2, 'Вкусно — и точка', 'Быстрое питание с бургерами и картофелем фри.', 'Вкусно — и точка — популярная сеть быстрого питания, расположенная на фуд-корте ТРЦ Атриум (Земляной Вал, 33, 3-й этаж). Предлагает широкий выбор бургеров, картофеля фри и десертов.', '10:00–22:00', '3-й этаж', 'vkusno_store.jpg', 'Фастфуд', 'Быстрое обслуживание, детское меню.', 'Используйте терминалы самообслуживания для быстрого заказа. В часы пик (12:00–14:00) возможны очереди.'),
    (2, 'Якитория', 'Японская кухня с суши и роллами.', 'Якитория — ресторан японской кухни, расположенный на 3-м этаже ТРЦ Атриум. Идеальное место для любителей суши, роллов и азиатских блюд.', '10:00–22:00', '3-й этаж', 'yakitoriya_store.jpg', 'Японская', 'Свежие ингредиенты, доставка через приложение.', 'Бронируйте столик заранее в выходные. Пробуйте сезонные сеты.'),
    (2, 'Starbucks', 'Кофейня с напитками и десертами.', 'Starbucks — всемирно известная кофейня, расположенная на 1-м этаже ТРЦ Атриум. Предлагает кофе, чай, десерты и снэки.', '10:00–22:00', '1-й этаж', 'starbucks_store.jpg', 'Кофейня', 'Сезонные напитки, программа лояльности.', 'Используйте приложение Starbucks для предзаказа. Утром (8:00–10:00) бывают очереди.'),
    (2, 'KFC', 'Фастфуд с куриными блюдами.', 'KFC — сеть быстрого питания, специализирующаяся на куриных блюдах, расположенная на фуд-корте ТРЦ Атриум (3-й этаж).', '10:00–22:00', '3-й этаж', 'kfc_store.jpg', 'Фастфуд', 'Комбо-меню, акции на доставку.', 'Проверяйте акции в приложении KFC. Используйте киоски самообслуживания.')
INSERT INTO kids_zones (category_id, name, short_description, detailed_description, working_hours, floor, image_url, age_range, features, visitor_tips) VALUES
    (3, 'Лабиринт', 'Игровая площадка с лабиринтами и горками.', 'Игровая площадка «Лабиринт» на 3-м этаже ТРЦ Атриум (Земляной Вал, 33) — это увлекательное пространство с многоуровневыми лабиринтами, горками и туннелями для детей.', '10:00–20:00', '3-й этаж', 'labyrinth_zone.jpg', '3–10 лет', 'Безопасные материалы, зоны для родителей.', 'Приходите в будни утром, чтобы избежать толпы. Убедитесь, что ребёнок в удобной одежде и носках.'),
    (3, 'Прыг-Скок', 'Батутный центр для активных детей.', 'Батутный центр «Прыг-Скок» на 3-м этаже ТРЦ Атриум — это место для активных игр с батутами и поролоновой ямой.', '10:00–20:00', '3-й этаж', 'jump_zone.jpg', '4–12 лет', 'Инструкторы, безопасные батуты.', 'Бронируйте сеанс заранее в выходные. Дети должны быть в спортивной одежде и специальных носках.'),
    (3, 'Творим Вместе', 'Мастер-классы по рисованию и лепке.', 'Мастер-классы «Творим Вместе» на 3-м этаже ТРЦ Атриум предлагают детям занятия по рисованию, лепке и созданию поделок.', '12:00–18:00', '3-й этаж', 'create_zone.jpg', '5–12 лет', 'Профессиональные педагоги, экологичные материалы.', 'Записывайтесь на мастер-классы заранее через сайт ТРЦ. Приносите сменную одежду.'),
    (3, 'Весёлые Герои', 'Анимационные шоу с персонажами.', 'Анимационная программа «Весёлые Герои» на 3-м этаже ТРЦ Атриум — это интерактивные шоу с любимыми персонажами для детей.', '14:00–19:00', '3-й этаж', 'heroes_zone.jpg', '3–10 лет', 'Профессиональные аниматоры, интерактивные игры.', 'Проверяйте расписание шоу на сайте ТРЦ. Приходите за 10 минут до начала.')
INSERT INTO cinemas (category_id, name, short_description, detailed_description, working_hours, floor, image_url, features, visitor_tips) VALUES
    (4, 'KAPO Max', 'Современный кинотеатр с IMAX.', 'KAPO Max — современный кинотеатр на 4-м этаже ТРЦ Атриум (Земляной Вал, 33), предлагающий сеансы в форматах IMAX, 3D и Dolby Atmos.', '10:00–23:00', '4-й этаж', 'kapo_max.jpg', 'IMAX, Dolby Atmos, VIP-залы.', 'Покупайте билеты онлайн, чтобы выбрать лучшие места. Приходите за 15 минут до сеанса.')
